import styled from "styled-components";

export const StyledMenu = styled.nav`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  width: 100%;
  text-align: left;
  padding: 2rem;
  position: absolute;
  top: 0;
  left: 0;
  background: #0d0f1b;
  overflow:hidden;
  z-index: 999;
  transition: transform 0.3s ease-in-out;
//   transform: ${({ open }) => (open ? "translateX(0)" : "translateX(-100%)")};

  @media (max-width: 777px) {
    width: 100%;
  }
  ul{
      position:relative;
      display:flex;
      flex-direction:column;
  }

  ul li{
    list-style:none;
}
  ul li a {
    font-size: 2rem;
    text-transform: uppercase;
    padding: 3px 15px;
    // font-weight: bold;
    letter-spacing:2px;
    color: white;
    overflow:hidden;
    text-decoration: none;
    transition: color 0.3s linear;
    background-color:#2196f3;
    text-transform:uppercase;
    font-s

    @media (max-width: 555) {
      font-size: 1.5rem;
      text-align: center;
    }

    &:hover {
      color: red;
    }
  }
`;
