import React from "react";
// import { useForm } from "react-hook-form";
import ReactDOM from "react-dom";
import { StyledButton } from "/home/khanna/Desktop/Productive/SelfProjects/Portfolio/Portfolio (Main)/src/components/Button/Button.styled.js";
import { StyledForm } from "./Form.styled";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      mobile: "",
      company: "",
      email: "",
      message: "",
      error: "",
      thankyou: false,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleBlur = this.handleBlur.bind(this);
  }

  isValidEmail(email) {
    return /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email);
  }

  isValidMobile(mobileno) {
    return /^[6-9]\d{9}$/.test(mobileno);
  }

  validateField(field, value) {
    if (value.length <= 0) {
      return (
        <div className="alert alert-danger">
          <span className="text-capitalize">{field}</span> is required field.
        </div>
      );
    } else {
      if (field == "email") {
        if (!this.isValidEmail(value))
          return <div className="alert alert-danger">Invalid Email.</div>;
      } else if (field == "mobile") {
        if (!this.isValidMobile(value))
          return (
            <div className="alert alert-danger">Invalid Mobile Number.</div>
          );
      } else {
        return "";
      }
    }
  }

  handleBlur(event) {
    this.setState({
      error: this.validateField(event.target.name, event.target.value),
    });
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  handleSubmit(event) {
    this.setState({
      thankyou: true,
    });

    event.preventDefault();
  }

  render() {
    if (!this.state.thankyou) {
      return (
        <StyledForm>
        <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5 container">
          <div className="card-header f_20_22 border-0 text-center">
            {this.props.title}
          </div>
          <div className="card-body">
            {this.state.error}

            <form
              onSubmit={this.handleSubmit}
              encType="multipart/form-data"
              autoComplete="off"
            >
              <div className="position-relative form-group ele">
                <input
                  name="name"
                  type="text"
                  className="text-field form-control mb-3 bg_grey border-0 py-3"
                  placeholder="Name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  required="required"
                />
              </div>
              {/* <div className="position-relative form-group">
                <input
                  name="mobile"
                  type="text"
                  className="text-field form-control mb-3 bg_grey border-0 py-1"
                  placeholder="Mobile"
                  onBlur={this.handleBlur}
                  value={this.state.mobile}
                  onChange={this.handleChange}
                />
              </div> */}
              {/* <div className="position-relative form-group">
                <input
                  name="company"
                  type="text"
                  className="text-field form-control mb-3 bg_grey border-0 py-1"
                  placeholder="Company"
                  value={this.state.company}
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  required="required"
                />
              </div> */}
              <div className="position-relative form-group ele">
                <input
                  name="email"
                  type="email"
                  className="text-field form-control mb-3 bg_grey border-0 py-1"
                  placeholder="Email"
                  value={this.state.email}
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  required="required"
                />
              </div>
              <div className="position-relative form-group ele text">
                <textarea
                  name="message"
                  type="text"
                  className="text-field form-control mb-3 bg_grey border-0 py-1"
                  placeholder="Message"
                  value={this.state.message}
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  required="required"
                />
              </div>
              <p className="text-center mb-0">
                <StyledButton>
                  <input
                    type="submit"
                    className=""
                    value="Submit Now"
                  />
                </StyledButton>
              </p>
            </form>
          </div>
        </div>
        </StyledForm>
      );
    }

    if (this.state.thankyou) {  
      return (
        <div className="thankyou_details">
          <p>Thank for your message. We will contact you soon.</p>
          <ul className="list-group">
            <li className="list-group-item">Name: {this.state.name}</li>
            <li className="list-group-item">Mobile: {this.state.mobile}</li>
            <li className="list-group-item">Company: {this.state.company}</li>
            <li className="list-group-item">Email: {this.state.email}</li>
            <li className="list-group-item">Message: {this.state.message}</li>
          </ul>
        </div>
      );
    }
  }
}

ReactDOM.render(<Form title="Get In Touch" />, document.getElementById("root"));

export default Form;
